import React, { useState } from 'react'
import { Row, Form, Col } from 'react-bootstrap';
import { Question } from "./data/data";

const FormInput = ({ add }) => {
    const [qu, setQu] = useState('');
    const [qa, setQa] = useState('');

    const addnewItem = () => {
        Question.push({ id: Math.random, q: qu, a: qa })
        setQu('')
        OnAdd();
    }
    return (
        <Row className="my-3">

            <Col sm="5">
                <Form.Control type="text" value={qu} onChange={() => setQu(e, target.value)} placeholder="ادخل السوال" />
            </Col>

            <Col sm="5">
                <Form.Control type="text" value={qa} onChange={() => setQa(e, target.value)} placeholder="ادخل الاجابه" />
            </Col>

            <Col sm="2">
                <button onClick={addnewItem} className="btn-color w-100" type="submit">
                    اضافة
                </button>
            </Col>

        </Row>
    )
}

export default FormInput
