import { Accordion, Button, Row } from "react-bootstrap";
import { Question } from "../data/data";

const QA = ({ dataview, deleteOneItem }) => {
    // 
    const onDeleteItem = (ID) => {
        if (dataview.length >= 1) {
            const index = Question.findIndex((i) => i.id === ID);
            Question.splice(0, Question.length)
        }
    }
    return (
        <>
            <Row>
                <Accordion>
                    {
                        dataview.length >= 1 ? (dataview.map((item, index) => {
                            return (
                                <Accordion.Item key={index} eventKey={item.id}><Accordion.Header>
                                    <div className="m-auto">{item.q}</div>
                                </Accordion.Header>
                                    <Accordion.Body>
                                        <div className="px-3 d-inline " >{item.a} </div>
                                        <Button onClick={() => { onDeleteItem(item.id) }} className="btn-color">مسح السؤال </Button>
                                    </Accordion.Body>
                                </Accordion.Item>
                            )
                        })) : <h2 className="fs-4 text-center">لا توجد اسئلة</h2>
                    }


                </Accordion>
            </Row >
        </>
    )
}
export default QA;
