import React, { useState } from "react";
import { Accordion, Button, Row } from "react-bootstrap";

const QA = ({ dataview }) => {
    const [activeItem, setActiveItem] = useState(null);

    const toggleAccordionItem = (itemID) => {
        if (activeItem === itemID) {
            // إغلاق الـ Accordion.Item إذا تم النقر على العنصر المفتوح بالفعل
            setActiveItem(null);
        } else {
            // فتح الـ Accordion.Item إذا تم النقر على عنصر جديد
            setActiveItem(itemID);
        }
    };

    return (
        <>
            <Row>
                <Accordion activeKey={activeItem}>
                    {dataview.length >= 1 ? (
                        dataview.map((item, index) => {
                            return (
                                <Accordion.Item
                                    key={index}
                                    eventKey={item.id}
                                    onClick={() => toggleAccordionItem(item.id)}
                                >
                                    <Accordion.Header>{item.q}</Accordion.Header>
                                    <Accordion.Body>
                                        <div className="px-3 d-inline">{item.a}</div>
                                        <Button className="btn-color">مسح السؤال</Button>
                                    </Accordion.Body>
                                </Accordion.Item>
                            );
                        })
                    ) : (
                        <h2 className="fs-4 text-center">لا توجد أسئلة</h2>
                    )}
                </Accordion>
            </Row>
        </>
    );
};

export default QA;
