import { Col, Container, Row } from "react-bootstrap";
import FormInput from "./Component/FormInput";
import QA from "./Component/QA";
import { Question } from "./data/data";
import { useState } from "react";

function App() {
  const [data, setData] = useState(Question);

  const AddItem = () => {
    setData([...Question])
  }

  const deleteQu = () => {
    Question.splice(0, Question.length)
    // السطر الي فوق بيحكي انه يمسحلي من العنصر الاولاني الى اخر عنصر 
    setData([])
    // اللسطر الي فوق بيحكي انه فضيلي الارري كلها 
  }


  const deleteOneItem = ()=>{}
  return (
    <div className="font color-body">
      <Container className="p-5">
        <Row className="justify-content-center">
          <Col sm="4">
            <div className="fs-3 text-center  py-2">اسئلة واجوبة شائعة</div>
          </Col>
          <Col sm="8">
            <FormInput OnAdd={AddItem} />
            <QA dataview={data} />
            {
              data.length >= 1 ? (<button onClick={deleteQu} className="btn-color w-100 my-3">مسح الكل</button>) : null
            }
          </Col>

        </Row>
      </Container>
    </div>
  );
}

export default App;
